import React, { Component } from 'react';
import { Route } from 'react-router-dom';
import { Link } from 'react-router-dom';
import  { Redirect } from 'react-router-dom';

class Profile extends Component{
  constructor(props) {
    super(props);
    this.createTicketButton = this.createTicketButton.bind(this)
    this.state  = {
        update: false,
        table: [
        	["Иванов", "Забрать груз со склада", "Увидел", "до вторника", "не определено", "ред."]

        ]
    };
  }
  createTicketButton(){
  	alert ("Вы похвалили сотрудника")
  }

	render(){
		let firstButton = !this.state.update 
		? "boss__createTicket"
		: "boss__displayNO"
		return(
			<div className="boss_container"> 
				<div className={firstButton}>
					<button 
							className="btn btn-success"
							onClick={this.createTicketButton}> Похвалить сотрудника </button>
				</div>
				<br />


				<div className="boss__table">
				<table>
					<tr>
						<td> Процесс </td>
						<td> Состояние </td>
						<td> Срок </td>
						<td> Время выполнения </td>
						<td> Редактировать </td>
					</tr>

					{
						this.state.table.map( line =>
						<>
						<td> {line[1]} </td>
						<td> {line[2]} 						
							<select  className="custom-select boss__input" 
			                 onChange={this.first_change}>
			          <option value="selected">Иванов </option>
			        </select>
			      </td>
						<td> {line[3]} </td>
						<td> {line[4]} </td>
						<td> {line[5]} </td>
						</>
						)
					}
				</table>
				</div>



			</div>
			)
	}
}
export default Profile;
