import  React, { Component, useState }  from 'react';
import  { Route } from 'react-router-dom';

import  { Link } from 'react-router-dom';
import  leftPNG from "./img/left.png";
import  rightPNG from "./img/right.png";
import  deletePNG from "./img/delete.png";
import  upgradePNG from "./img/upgrade.png";

class Admin extends Component{

  constructor(props) {
    super(props);

    this.state = {
      catalog: [],
      type: "newArticles",
      newArticles: [],
      selectedArticle: null
    };
  }
  componentDidMount(){
    var myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");
    var requestOptions = {
      method: 'GET',
      headers: myHeaders,
      redirect: 'follow'
    };
    fetch('http://127.0.0.1:8000/allcatalog', requestOptions)
      .then(response => response.json())
      .then(result => {
        let arr = []
        for (let i=0; i<result.length; i++){
          for (let j=1; j<result[i].length; j++){  
            for (let z=1; z<result[i][j].length; z++){
              for (let k=1; k<result[i][j][z].length; k++){
                if (result[i][j][z][k][2] && result[i][j][z][k][2] === "unmod"){
                  arr.push(result[i][j][z][k])
                  console.log(result[i][j][z][k])
                }
              }
            }
          }
        }

        this.setState({
          catalog: result,
          newArticles: arr,
          selectedArticle: arr.length > 0 ? 0 : null
        })
      })
  }

  componentDidUpdate(prevState) {
    if (prevState.selectedArticle !== this.state.selectedArticle){
        var myHeaders = new Headers();
        var raw = JSON.stringify({"id": this.state.newArticles[this.state.selectedArticle]});
        myHeaders.append("Content-Type", "application/json");
        var requestOptions = {
          method: 'POST',
          body: raw,
          headers: myHeaders,
          redirect: 'follow'
        };
        fetch('http://127.0.0.1:8000/article', requestOptions)
          .then(response => response.json())
          .then(result => {
            console.log(result[0].article)
            this.setState({    
                  article: result[0].article
                })
          })
    }
  }

  type_change(event) {
    this.setState({type: event.target.value})
  }






  render() {
    let newArticlesState = this.state.type === "newArticles"
    ? ""
    : "admin__nonDisplay"

    return(
      <div className="admin__container">

        <select  className="custom-select mr-sm-2" 
                 id = "inlineFormCustomSelect" 
                 onChange={this.type_change}>
          <option value="newArticles"> Новые статьи </option>
        </select>




        <div className={newArticlesState}>
          <div className="admin__newArticlesSelectorBlock">
            <img 
              className="admin__selector"     
              alt="left"
              src={leftPNG}/>            
            <img 
              className="admin__selector"     
              alt="right"
              src={rightPNG}/>            
            <img 
              className="admin__selector"     
              alt="delete"
              src={deletePNG}/>            
            <img 
              className="admin__selector"     
              alt="upgrade"
              src={upgradePNG}/>
          </div>

          <div className="admin__grade"> </div>

          <div className="admin__selectedArticle">
            <table>
                      { 
                        this.state.newArticles[this.state.selectedArticle].map( second  =>
                          <tr> 
                            <td> </td>
                            <td> </td>
                          </tr>

                        )
                      }
            </table>
          </div>

        </div>






      </div>
    );
  }
}

export default Admin;
