import React, { Component } from 'react';
import { Route } from 'react-router-dom';
import { Link } from 'react-router-dom';
import  { Redirect } from 'react-router-dom';

class Boss extends Component{
  constructor(props) {
    super(props);
    this.createTicketButton = this.createTicketButton.bind(this)
    this.state  = {
        update: false,
        table: [
        	["Иванов", "Забрать груз со склада", "Просмотрел", "до вторника", "не определено", "ред."]
        ]

    };
  }
  createTicketButton(){
  	this.setState({update: !this.state.update})
  }

	render(){
		let firstButton = !this.state.update 
		? "boss__createTicket"
		: "boss__displayNO"
		let secondButton = this.state.update
		? "boss__inputGroupButton btn btn-success"
		: "boss__displayNO"
		let inputGroup = this.state.update
		? "boss__inputBlock"
		: "boss__displayNO"
		return(
			<div className="boss_container"> 
				<div className={firstButton}>
					<button 
							className="btn btn-success"
							onClick={this.createTicketButton}> добавить задачу </button>
				</div>
				<br />

				<div className={inputGroup}>
						<select  className="custom-select boss__input" 
		                 onChange={this.first_change}>
		          <option value="selected">Иванов </option>
		        </select>
						<input className="boss__input form-control" placeholder="Процесс"/>
    				<label for="inputDate">Сделать до:</label>
						<input type="date" className="boss__input form-control"/>
				</div>
				<button 
							className={secondButton}
							> Добавить задачу </button>
				<br />

				<div className="boss__table">
				<table>
					<tr>
						<td> Работник </td>
						<td> Процесс </td>
						<td> Состояние </td>
						<td> Срок </td>
						<td> Время выполнения </td>
						<td> Редактировать </td>
					</tr>

					{
						this.state.table.map( line =>
						<>
						<td><Link to="/profile"> {line[0]} </Link></td>
						<td> {line[1]} </td>
						<td> {line[2]} </td>
						<td> {line[3]} </td>
						<td> {line[4]} </td>
						<td> {line[5]} </td>
						</>
						)
					}
				</table>
				</div>



			</div>
			)
	}
}
export default Boss;
