  constructor(props) {
    super(props);

    this.state = {
      article:  [
                  ["Установка React", "header"],
                  ["Первым делом нужно установить NodeJS", "text"],
                  ["Затем в терминале пишем:", "text"],
                  ["npm install -g create-react-app", "code"],
                  ["Затем создаим наше приложение, где my-app это назавание приложения", "text"],
                  ["create-react-app my-app", "code"],
                  ["Перейдем в директорию проекта", "text"],
                  ["cd my-app", "code"],
                  ["Запустим приложение", "text"],
                  ["npm start", "code"]
                ]
    };
  }
  componentDidMount(){
    const { match: { params } } = this.props;
    this.setState({opend: params.pk});
    var myHeaders = new Headers();
    var raw = JSON.stringify({"id":"11111111111"});
    myHeaders.append("Content-Type", "application/json");
    var requestOptions = {
      method: 'POST',
      body: raw,
      headers: myHeaders,
      redirect: 'follow'
    };
    fetch('http://127.0.0.1:8000/article', requestOptions)
      .then(response => response.json())
      .then(result => {
        console.log(result[0].article)
        this.setState({        })
      })
  }


  componentDidUpdate(prevProps) {
    const { match: { params } } = this.props;
    if (params.pk !== this.state.opend) {
      this.setState({opend: params.pk});
    }
  }

  render() {

    return(
      <>
        { 
          this.state.article.map( moment  =>
            <div key={moment} className="">
              {moment[0]}
            </div>
          )
        }
      </>
    );
  }
}

export default Article;
