import React, { Component } from 'react';
import { Route } from 'react-router-dom';
import { Link } from 'react-router-dom';
import  { Redirect } from 'react-router-dom';

class Boss extends Component{
  constructor(props) {
    super(props);
    this.state  = {
        update: false,
        table: [
        ["Иванов", "Забрать груз со склада", "Увидел", "до вторника", "не определено", "ред."]

        ]
    };
  }
  createTicketButton(){
  	this.setState({update: true})
  }

	render(){
		return(
			<div className="boss_container"> 
				<div className="boss__createTicket">
					<button 
							className="btn btn-success"
							onClick={this.createTicketButton}> добавить задачу </button>
				</div>
				<br />

				<div className="boss__inputBlock">
				<input className="boss__input" />
				</div>

				<div className="boss__table">
				<table>
					<tr>
						<td> Работник </td>
						<td> Процесс </td>
						<td> Состояние </td>
						<td> Срок </td>
						<td> Время выполнения </td>
						<td> Редактировать </td>
					</tr>

					{
						this.state.table.map( line =>
						<>
						<td> {line[0]} </td>
						<td> {line[1]} </td>
						<td> {line[2]} </td>
						<td> {line[3]} </td>
						<td> {line[4]} </td>
						<td> {line[5]} </td>
						</>
						)
					}
				</table>
				</div>



			</div>
			)
	}
}
export default Boss;
