import React, { Component, useState }  from 'react';
import { Route } from 'react-router-dom';

import { Link } from 'react-router-dom';



class CreateArticle extends Component{

  constructor(props) {
    super(props);

    this.state = {
      catalog:  [],
      first_value: "",
      second_value
    };
  }
  componentDidMount(){
    var myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");
    var requestOptions = {
      method: 'GET',
      headers: myHeaders,
      redirect: 'follow'
    };
    fetch('http://127.0.0.1:8000/allcatalog', requestOptions)
      .then(response => response.json())
      .then(result => {
        console.log(result)
        this.setState({
          catalog: result
        })
      })
  }


  componentDidUpdate(prevProps) {
    const { match: { params } } = this.props;
    if (params.pk !== this.state.opend) {
      this.setState({opend: params.pk});
    }
  }

  render() {

    return(
      <div className="create-article__container">
        <div class="create-article__change col-auto my-1">
          <label class="mr-sm-2 sr-only" for="inlineFormCustomSelect">Preference</label>
          <select class="custom-select mr-sm-2" id="inlineFormCustomSelect">
            <option value="selected">Choose...</option>
            { 
              this.state.catalog.map( global  =>
                <option value={global[0]}>{global[0]}</option>
              )
            }
          </select>
        </div>
      </div>
    );
  }
}

export default CreateArticle;
