import  React, { Component, useState }  from 'react';
import  { Route } from 'react-router-dom';

import  { Link } from 'react-router-dom';
import  leftPNG from "./img/left.png";
import  rightPNG from "./img/right.png";
import  deletePNG from "./img/delete.png";
import  upgradePNG from "./img/upgrade.png";

class Admin extends Component{

  constructor(props) {
    super(props);

    this.state = {
      catalog: [],
      type: "newArticles"
    };
  }
  componentDidMount(){
    var myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");
    var requestOptions = {
      method: 'GET',
      headers: myHeaders,
      redirect: 'follow'
    };
    fetch('http://127.0.0.1:8000/allcatalog', requestOptions)
      .then(response => response.json())
      .then(result => {
        this.setState({
          catalog: result
        })
      })
  }
  type_change(event) {
    this.setState({type: event.target.value})
  }


  render() {
    let newArticlesState = this.state.type === "newArticles"
    ? ""
    : "admin__nonDisplay"

    return(
      <div className="admin__container">

        <select  className="custom-select mr-sm-2" 
                 id = "inlineFormCustomSelect" 
                 onChange={this.type_change}>
          <option value="newArticles"> Новые статьи </option>
        </select>


        <div className={newArticlesState}>
          <div className="admin__newArticlesSelectorBlock">
            <img 
              className="admin__selector"     
              alt="left"
              src={leftPNG}/>            
            <img 
              className="admin__selector"     
              alt="right"
              src={rightPNG}/>            
            <img 
              className="admin__selector"     
              alt="delete"
              src={deletePNG}/>            
            <img 
              className="admin__selector"     
              alt="upgrade"
              src={upgradePNG}/>
          </div>
        </div>

      </div>
    );
  }
}

export default Admin;
