import React, { Component } from 'react';
import { Route } from 'react-router-dom';
import { Link } from 'react-router-dom';
import  { Redirect } from 'react-router-dom';
import {LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid, Legend} from 'recharts';
const data = [
	{ name: "6 мая 2020", выполнены: 3, pv:2, amt: 500},
	{ name: "7 мая 2020", выполнены: 7, pv:1, amt: 500},
	{ name: "8 мая 2020", выполнены: 5, pv:2, amt: 500},
	{ name: "9 мая 2020", выполнены: 5, pv:3, amt: 500},
	{ name: "10 мая 2020", выполнены: 2, pv:3, amt: 500},
	{ name: "11 мая 2020", выполнены: 6, pv:2, amt: 500},
	{ name: "12 мая 2020", выполнены: 5, pv:1, amt: 500},

]
class Profile extends Component{
  constructor(props) {
    super(props);
    this.createTicketButton = this.createTicketButton.bind(this)
    this.change = this.change.bind(this)
    this.state  = {
        update: false,
        table: [
        	["Иванов", "Забрать груз со склада", "Просмотрел", "до вторника", "не определено", "ред."]

        ]
    };
  }
  createTicketButton(){
  	alert ("Вы похвалили сотрудника")
  }
  backButton(){
  	  	this.setState({update: !this.state.update})

  }
  change(event){
  	let arr = this.state.table
  	console.log(event.target.value)
  	arr[0][2] = event.target.value
  	this.setState({table: arr})
  	console.log(this.state.table)
  }

	render(){
		let firstButton = !this.state.update 
		? "boss__createTicket"
		: "boss__displayNO"
		return(
			<div className="boss_container"> 
				<div className={firstButton}>
					<button 
							className="btn btn-success"
							onClick={this.createTicketButton}> Похвалить сотрудника </button>
				</div>				
				<div className={firstButton}>
					 <Link className="btn btn-warning" to="/boss"> Вернуться назад </Link>
				</div>
				<br />


				<div className="boss__table">
				<table>
					<tr>
						<td> Процесс </td>
						<td> Состояние </td>
						<td> Срок </td>
						<td> Время выполнения </td>
					</tr>

					{
						this.state.table.map( line =>
						<>
						<td> {line[1]} </td>
						<td>					
							<select  className="custom-select profile__input" 
								onChange={this.change}>
			          <option value="Не видел"> Не видел </option>

			          <option value="Просмотрел"> Просмотрел </option>
			          <option value="Исполняю"> Исполняю </option>			          
			          <option value="Есть проблема"> Есть проблема </option>
			          <option value="Сделал"> Сделал </option>         			          


			        </select>
			      </td>
						<td> {line[3]} </td>
						<td> {line[4]} </td>
						</>
						)
					}
				</table>
				</div>


				<h3 className="profile__hGraphics"> Статистика выполненых во время заданий </h3>
				<p className="profile__XTitle"> Колличество заданий </p>

				<LineChart
				  width={800}
				  height={400}
				  data={data}
				  margin={{ top: 5, right: 20, left: 10, bottom: 5 }}
				>
				  <XAxis dataKey="name" />
	        <YAxis />

				  <Tooltip />
				  <CartesianGrid stroke="#f5f5f5" />
				  <Line type="monotone" dataKey="выполнены" stroke="#66ff00" />
				  <Line type="monotone" dataKey="pv" stroke="#FF0000" />
				  <Legend />
				</LineChart>
				<p className="profile__YTitle"> День </p>

			</div>
			)
	}
}
export default Profile;
